import React, { useState,useEffect,use } from "react";
import { TextField,Button,Typography,Paper} from '@material-ui/core';
import useStyles from "./styles.js"
import FileBase from 'react-file-base64'; 
import {useDispatch} from 'react-redux';
import {useSelector} from 'react-redux';
import {createPost,updatePost} from '../../actions/posts';


function Form({currentId , setCurrentId}){
    const [postData , setPostData] = useState({
        creator:'',
        title:'',
        message:'',
        tags:'',
        selectedFile:''
    });
    const post = useSelector((state) => currentId ? state.posts.find((p)=> p._id === currentId ): null);
    const classes = useStyles();
    const dispatch = useDispatch();

    useEffect(()=>{
        if(post)
            setPostData(post);
    },[post])

    const handleSubmit = (e) => {
         e.preventDefault();

        if(currentId){
            dispatch(updatePost(currentId,postData));
            clear();
        }else{
            dispatch(createPost(postData));
            clear();
        }

        
    }

    const clear = () => {
        setCurrentId(null);
        setPostData({
            creator:'',
            title:'',
            message:'',
            tags:'',
            selectedFile:''
        });
    }



    const inputRef=useRef("null")
    const [previewUrl,setPreviewUrl]=useState(null)

    const handleImageChange=(event)=>{
        const file=event.target.files[0]
        if(file)
        {
          setImage(file);  
        }
    }

    const oncChooseFile=()=>{
        inputRef.current.click()
    }


    const handleRemoveImage=()=>{
        setImage(null)
        handleRemoveImage()
}


    useEffect(() => {
      if(typeof image==='string'){
        setPreviewUrl(image)}
        else if(image){
            setPreviewUrl(URL.createObjectURL(image))
        }
        else{
            setPreviewUrl(null)
        }

        return ()=>{
            if(!previewUrl && typeof previewUrl==="string" && !image){
                URL.revokeObjectURL(previewUrl)
            }
        }
      
    }, [image])
    


    return (
        <>
         <Paper className={classes.paper}>
            <form autoComplete='off' noValidate  className={`${classes.root} ${classes.form}`} onSubmit={handleSubmit}>
                <Typography variant='h6'>{currentId ? 'Editing ': 'Creating'} a Memory</Typography>
                <TextField 
                    name='creator' 
                    variant='outlined' 
                    label='Creator' 
                    fullWidth
                    value={postData.creator}
                    onChange={(e)=> setPostData({...postData,creator: e.target.value})}  
                />

                <TextField 
                    name='title' 
                    variant='outlined' 
                    label='Title' 
                    fullWidth
                    value={postData.title}
                    onChange={(e)=> setPostData({...postData,title: e.target.value})} 
                />

                <TextField 
                    name='message' 
                    variant='outlined' 
                    label='Message' 
                    fullWidth
                    value={postData.message}
                    onChange={(e)=> setPostData({...postData,message: e.target.value})} 
                />

                <TextField 
                    name='tags'
                    variant='outlined'
                    label='Tags' 
                    fullWidth
                    value={postData.tags}
                    onChange={(e)=> setPostData({...postData,tags: e.target.value.split(',')})} 
                />
                <div className={classes.fileInput}>
                    <FileBase 
                        type='file'
                        multiple = {false}
                        onDone = {({base64})=> setPostData({...postData,selectedFile: base64})}
                    />
                </div>

                <div>
        <input
        type='file'
        accept='image/*'
        ref={inputRef}
        onChange={handleImageChange}
        className='hidden'
        />

      {!image ?  <button className='w-full h-[220px] flex flex-col items-center justify-center gap-4 bg-slate-50 rounded border border-slate-200/50' 
        onClick={()=>{oncChooseFile()}}>
            <div className='w-14 h-14 flex items-center justify-center bg-cyan-50 rounded-full border border-cyan-100'>
                <FaRegImage className='text-xl text-cyan-500'/>
            </div>
            <p className='text-sm text-slate-500'>Browse image files to upload</p>
        </button>
        :
        <div className='w-full relative'>
            <img 
            src={previewUrl}
            alt='Selected'
            className='w-full h-[300px] object-cover rounded-lg'/>
            <button className='btn-small btn-delete absolute top-2 right-2'
            onClick={handleRemoveImage}>
                <MdDeleteOutline className='text-lg'/>
            </button>
            </div>
}
    </div>


                <Button className={classes.buttonSubmit} variant='contained' color='primary' size='large' type='submit' fullWidth>Submit</Button>
                <Button variant='contained' color='secondary' size='small' onClick={clear} fullWidth>Clear</Button>
            </form>
         </Paper>
        </>
    )
}

export default Form;
